(
    athlete_id	INT,
    games	VARCHAR(512),
    year	INT,
    season	VARCHAR(512),
    city	VARCHAR(512),
    sport	VARCHAR(512),
    event	VARCHAR(512),
    medal	VARCHAR(512)
);




select * from athletes limit 10;
select * from athlete_events limit 10;


-- 1. which team has won the maximum gold medals over the years.

select athletes.team, count(distinct athlete_events.event) cnt 
from athletes inner join athlete_events
on athletes.id = athlete_events.athlete_id
where athlete_events.medal = 'Gold'
group by athletes.team
order by cnt desc;

-- 2 for each team print total silver medals and year in which they won maximum silver medal..output 3 columns
-- team,total_silver_medals, year_of_max_silver

select * from athletes limit 10;
select * from athlete_events limit 10;


with cte as(
select athletes.team, athlete_events.year, count(distinct athlete_events.event) silver_eve,
rank() over(partition by athletes.team order by count(distinct athlete_events.event)) rn
from athletes inner join athlete_events
on athletes.id = athlete_events.athlete_id
where athlete_events.medal = 'Silver'
group by athletes.team, athlete_events.year)
select team, sum(silver_eve) total_silver, 
max(case when rn=1 then year end) year_max_silver
from cte 
group by team;


-- 3 which player has won maximum gold medals  amongst the players 
-- which have won only gold medal (never won silver or bronze) over the years

select * from athletes limit 10;
select * from athlete_events limit 10;


with cte as (
select name,medal
from athlete_events ae
inner join athletes a on ae.athlete_id=a.id) 
select name, count(1) no_of_gold
from cte
where name not in(select distinct name from cte where medal in ('Silver','Bronze'))
and medal='Gold'
group by name
order by no_of_gold desc;

-- 4 in each year which player has won maximum gold medal . Write a query to print year,player name 
-- and no of golds won in that year . In case of a tie print comma separated player names.

with cte as (
select ae.year,a.name,count(1) as no_of_gold
from athlete_events ae
inner join athletes a on ae.athlete_id=a.id
where medal='Gold'
group by ae.year,a.name)
select year,no_of_gold,group_concat(name) as players from (
select *,
rank() over(partition by year order by no_of_gold desc) as rn
from cte) a where rn=1
group by year,no_of_gold;


-- 5 in which event and year India has won its first gold medal,first silver medal and first bronze medal
-- print 3 columns medal,year,sport
select distinct * from (
select medal,year,event,rank() over(partition by medal order by year) rn
from athlete_events ae
inner join athletes a on ae.athlete_id=a.id
where team='India' and medal != 'NA'
) A
where rn=1;


-- 6 find players who won gold medal in summer and winter olympics both.
select a.name  
from athlete_events ae
inner join athletes a on ae.athlete_id=a.id
where medal='Gold'
group by a.name having count(distinct season)=2;


-- 7 find players who won gold, silver and bronze medal in a single olympics. print player name along with year.
select year,name
from athlete_events ae
inner join athletes a on ae.athlete_id=a.id
where medal != 'NA'
group by year,name having count(distinct medal)=3;

-- 8 find players who have won gold medals in consecutive 3 summer olympics in the same event . Consider only olympics 2000 onwards. 
-- Assume summer olympics happens every 4 year starting 2000. print player name and event name.
with cte as (
select name,year,event
from athlete_events ae
inner join athletes a on ae.athlete_id=a.id
where year >=2000 and season='Summer'and medal = 'Gold'
group by name,year,event)
select * from
(select *, lag(year,1) over(partition by name,event order by year ) as prev_year
, lead(year,1) over(partition by name,event order by year ) as next_year
from cte) A
where year=prev_year+4 and year=next_year-4;
 







