CREATE TABLE happiness_index (
    con_rank	INT,
    Country	VARCHAR(512),
    Happiness_2021 	DOUBLE,
    Happiness_2020	DOUBLE,
    2022_Population	INT
);



select * from happiness_index;



select *, case when Country='India' then 1 else 0 end con 
from happiness_index
order by Happiness_2020 desc;


select * from 
(select *, case when Country='India' then 1 else 0 end con 
from happiness_index) A
order by con desc, Happiness_2020 desc;

